# coding=utf-8
import os

from nd.rest.nd_uc import UcEnv
from nd.rest.uc.config.token_t import TokenType
from nd.rest.uc.config.iconfig import IConfig


__author__ = 'linzh'


class UcConfig(IConfig):
    def __init__(self, env=UcEnv.ol):
        self.env = env
        self.ty = TokenType.MAC093
        self.appid = ""
        self.username = ""
        self.password = ""
        self.org = ""
        self.ssl = True

    def set_env(self, env):
        self.env = env

    def set_token_type(self, ty, appid=""):
        self.ty = ty
        if ty == TokenType.MAC110:
            if appid == "":
                raise Exception("使用UC1.1的Mac Token，请传入参数appid, 格式：[appid=uuid]")
            else:
                self.appid = appid

    def get_token_type(self):
        return self.ty

    def get_appid(self):
        return self.appid

    def get_env(self):
        return self.env

    def get_username(self):
        return self.username

    def get_password(self):
        return self.password

