# coding=utf-8

from .nd_uc_new import *


class NdUcNew(NdUc):
    '''
    新增环境
    '''
    def __init__(self, uc_config, env, uc_version):
        NdUc.__init__(self)
        self.env = env
        self.uc_version = str(uc_version)
        self.uc_host_uc_0_93 = uc_config.uc_host_uc_0_93
        self.uc_host_uc_1_1 = uc_config.uc_host_uc_1_1
        if uc_config.header != None:
            self.header = uc_config.header
        else:
            self.header = {
                "Content-Type": "application/json"
            }
        if self.uc_version == '0.93':
            self.host = self.uc_host_uc_0_93
        elif self.uc_version == '1.1':
            self.host = self.uc_host_uc_1_1
        else:
            raise Exception("UC认证服务版本未传或者指定有误！仅支持0.93, 1.1")
        if not self.host:
            raise Exception("uc_host配置不正确！")


