# coding=utf-8
from nd.rest.nd_uc import UcEnv

__author__ = 'linzh'


class BtsConfig(object):
    def __init__(self):
        self.hosts = {
            UcEnv.pre: "https://betabts.101.com",
            UcEnv.ol: "https://ucbts.101.com",
            UcEnv.awsca: "https://ucbts.101.com",
            UcEnv.hk: "https://ucbts.101.com",
            UcEnv.snwjt: "https://uc-bts.sneduyun.com.cn",
            UcEnv.iraq: "http://uc-bts.iraq.101.com"
        }

    def get_host(self, env):
        return self.hosts[env]
