# coding=utf-8
from cof.uc.config.iconfig import IConfig
from cof.uc.config.token_t import TokenType
from cof.uc.mac.v1_1.tok import MacV11Token

__author__ = 'linzh'


class TokenFactory(object):
    def __init__(self, config):
        if not isinstance(config, IConfig):
            raise Exception("配置要为UC的配置类")
        self.config = config

    def create(self):
        config = self.config

        if config.get_token_type() == TokenType.MAC110:
            token_maker = MacV11Token()
        elif config.get_token_type() == TokenType.MAC093:
            token_maker = MacV11Token()

        return token_maker
